}

.margin-auto {
    margin: 0 auto;
}

.flex {
    display: flex;
}

.flex-column {
    flex-direction: column;
}

.flex-row {
    flex-direction: row;
}

.col {
    padding: 0 9px;
}

@for $i from 1 through 12 {

    .col-#{$i} {
        width: $i / 12 * 100%;
        padding: 0 9px;
    }
}

@for $i from 1 through 12 {
    .offset-l-#{$i} {
        padding-left: $i / 12 * 100%;
    }

    .offset-r-#{$i} {
        padding-right: $i / 12 * 100%;
    }
}
